import './ExpenseForm.css';
import React,{useState} from 'react';

const ExpenseForm=(props)=>{
    const [enteredTitle,setEnteredTitle]=useState('');
    const [enteredAmount,setEnteredAmount]=useState('');
    const [enteredDate,setEnteredDate]=useState('');

    // const [enteredInput,setEnteredInput]=useState({
    //     enteredTitle:'',
    //     enteredAmount:'',
    //     enteredDate:''
    // })



    const titleChangeHandler=(event)=>{
        setEnteredTitle(event.target.value)

        //used for updating state that depends on the value of the prev state
        // setEnteredInput((prevState)=>{
        //     return {...prevState,enteredTitle:event.target.value};
        // })
    }

    const amountChangeHandler=(event)=>{
        setEnteredAmount(event.target.value)

        //used for updating state that depends on the value of the prev state
        // setEnteredInput((prevState)=>{
        //     return {...prevState,enteredAmount:event.target.value};
        // })
    }

    const dateChangeHandler=(event)=>{
        setEnteredDate(event.target.value)

        //used for updating state that depends on the value of the prev state
        // setEnteredInput((prevState)=>{
        //     return {...prevState,enteredDate:event.target.value};
        // })
    }

    const submitHandler=(event)=>{
        //to prevent the default behaviour of the form that it reloads the page
        event.preventDefault()

        const expenseData={
            title:enteredTitle,
            amount:enteredAmount,
            date:new Date(enteredDate)
        };

        props.getNewExpenseData(expenseData)

        // console.log(expenseData)
        setEnteredTitle('')
        setEnteredAmount('')
        setEnteredDate('')

        props.onSubmitHandler()
    }

    return (
        
        <form onSubmit={submitHandler} onReset={resetHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text'value={enteredTitle} onChange={titleChangeHandler}></input>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01'value={enteredAmount} onChange={amountChangeHandler}></input>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2022-12-31' value={enteredDate} onChange={dateChangeHandler}></input>
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='reset'>Cancel</button>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
