import React,{useState} from "react"
import ExpenseItem from "./ExpenseItem"
import './Expenses.css'
import Cards from '../UI/Cards'
import ExpenseFilter from "./ExpenseFilter"

const Expenses=(props)=>{

    const [selectedYear,setSelectedYear]=useState('2020');

    const getSelectedYear=(val)=>{
        setSelectedYear(val);
        console.log(val);
    }

    const filteredExpenses=props.expenses.filter((expense)=>{
        return expense.date.getFullYear().toString() === selectedYear;
    })

    let expenseContent=<h2>No Expenses Found</h2>

    if(filteredExpenses.length>0)
    {
        expenseContent=filteredExpenses.map((ob)=>{
            return <ExpenseItem key={ob.id} date={ob.date} amount={ob.amount} title={ob.title}></ExpenseItem>
        })
    }


    return(
        //ExpenseFilter is said as controlled component
        <div>
            
            <Cards className="expenses">
            <ExpenseFilter selectedYear={selectedYear} getSelectedYear={getSelectedYear}></ExpenseFilter>
                {}
            </Cards>
        </div>
    )
}

export default Expenses;
